if (!function_exists('adminUrl')) {
    function adminUrl($section_path = null)
    {
        $query = [
            'mod' => $_GET['mod'],
            'id' => $_GET['id'],
            'sec' => $section_path ?? $_GET['p'] ?? '/'
        ];
        return $_SERVER['PHP_SELF'] . '?' . http_build_query($query);
    }
}

if (!function_exists('isActive')) {
    function isActive($section_path)
    {
        if (isset($_GET['sec']) && $_GET['sec'] === $section_path) {
            return ' active';
        }

        return '';
    }
}

if (!function_exists('loadAction')) {
    function loadAction()
    {
        if (isset($_GET['sec']) && $_GET['sec'] !== '') {
            $section_path = utility::filterData('sec', 'get', true, true, true);
            $file_path = __DIR__ . '/libs/' . $section_path . '_action.php';
            if (file_exists($file_path)) {
                include $file_path;
            }
        }
        if (count($_POST) > 0) {
            exit;
        }
    }
}

